  -> struct sock* sk = sk_alloc()
    -> sk_prot_alloc -> kmem_cache_alloc (tcp_prot is 1360B)
  -> sock_init_data
    sk->sk_socket = sock;
    sock->sk = sk;
  -> sk->sk_prot->init(sk)
    -> tcp_v4_init_sock
